import { useParams } from "react-router";
import { Redirect, Route, Switch} from "react-router-dom"
import data from "../dummy-data";

const Movie = () => {
  let {id} = useParams();

  const elements = data.find(element => element.mal_id == id);
  if(!elements){
   return(
    <Route render={isAdmin ? (
      <Redirect to='/confirm' />
  ) : (
          <NotFound />
      )
  } />
   )
  }else{

    const movie = {
      image_url: elements.image_url,
      title: elements.title,
      score: elements.score,
    };
  
  
    return (
      <div className="row my-5">
        <div className="card mb-3 p-0">
          <div className="row g-0">
            <div className="col-md-4">
              <img
                src={movie.image_url}
                className="img-fluid rounded-start"
                alt="skilvul"
              />
            </div>
            <div className="col-md-8">
              <div className="card-body">
                <h5 className="card-title">{movie.title}</h5>
                <p className="card-text">
                  This is a wider card with supporting text below as a natural
                  lead-in to additional content. This content is a little bit
                  longer.
                </p>
                <p className="card-text">
                  <small className="text-muted">{movie.score}</small>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  console.log(id)
  console.log(elements)
  // Variable berikut akan menampung data movie yang akan kita tampilkan
  
};

export default Movie;
